import {
    Streamlit,
    StreamlitComponentBase,
    withStreamlitConnection,
} from "streamlit-component-lib"
import React, {ReactNode} from "react"
// import Box from '@mui/material/Box';
// import Slider from '@mui/material/Slider';
// import {styled} from '@mui/material/styles';
import Chip from '@mui/material/Chip';
// import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
// import DoneIcon from '@mui/icons-material/Done';
// import DeleteIcon from '@mui/icons-material/Delete';


class SentencesClickLabel extends StreamlitComponentBase<any> {
    public state = {is_clicked: false}

    private handleClick = (e: string): void => {
        // console.info('You clicked the Chip.' + content);
        this.setState(
            // prevStatea=>({preState: !prevState.is_clicked}),
            () => Streamlit.setComponentValue(e),
        )
    };
    private handleDelete = (): void => {
        console.info('You clicked the delete icon.');
    };
    public render = (): ReactNode => {
        var items: string[];
        items = this.props.args["sentences"]
        // items = ["Google", "Runoob", "Taobao","dfdfdfsdfsdfsdfsdf",'sdfsdfsdfsdfsdf',"上三李四王二那，","上的发送到繁多发的3242343"]
        // @ts-ignore
        return (
            <Stack direction="row" spacing={0} sx={{
                display: 'flex',
                flexWrap: 'wrap',
                alignContent: 'flex-start',
                bgcolor: 'background.paper',
                maxWidth: "wrap",
                height: "wrap",
            }}>
                {items.map((item, index) => {
                        return (
                            <Chip
                                label={item}
                                onClick={this.handleClick.bind(this, item)}
                                variant="outlined"
                                size="small"
                                className={'ChipClass'}
                                sx={{
                                    borderRadius: 1,
                                    mb: 0.5,
                                    mr: 0.2,
                                display: 'inline',
                                    overflow:"visible"

                                }}
                            />
                        )
                    }
                )}
            </Stack>
        );
    }
}

export default withStreamlitConnection(SentencesClickLabel)
