        // }));
        // const [chipData, setChipData] = React.useState<readonly ChipData[]>([
        //     {key: 0, label: 'Angular'},
        //     {key: 1, label: 'jQuery'},
        //     {key: 2, label: 'Polymer'},
        //     {key: 3, label: 'React'},
        //     {key: 4, label: 'Vue.js'},
        // ]);

        const handleDelete = (chipToDelete: ChipData) => () => {
            setChipData((chips) => chips.filter((chip) => chip.key !== chipToDelete.key));
        };
        return (
            <Paper
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    flexWrap: 'wrap',
                    listStyle: 'none',
                    p: 0.5,
                    m: 0,
                }}
                component="ul"
            >
                {chipData.map((data) => {
                    let icon;

                    if (data.label === 'React') {
                        icon = <TagFacesIcon/>;
                    }
                    return (
                        <ListItem key={data.key}>
                            <Chip
                                icon={icon}
                                label={data.label}
                                onDelete={data.label === 'React' ? undefined : handleDelete(data)}
                            />
                        </ListItem>
                    );
                })}
            </Paper>
        );
    }
}

export default withStreamlitConnection(ComponentSlider)
