import {
    Streamlit,
    StreamlitComponentBase,
    withStreamlitConnection,
} from "streamlit-component-lib"
import React, {ReactNode} from "react"
import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';
import {styled} from '@mui/material/styles';
import Chip from '@mui/material/Chip';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import DoneIcon from '@mui/icons-material/Done';
import DeleteIcon from '@mui/icons-material/Delete';


class ComponentSlider extends StreamlitComponentBase<any> {
    public render = (): ReactNode => {
        const handleClick = (content) => {
            console.info('You clicked the Chip.'+content);
           () => Streamlit.setComponentValue(content)
        };
        const handleDelete = () => {
            console.info('You clicked the delete icon.');
        };
        return (
            <Stack direction="row" spacing={0} sx={{
                display: 'flex',
                flexWrap: 'wrap',
                alignContent: 'flex-start',
                p: 1,
                m: 1,
                bgcolor: 'background.paper',
                maxWidth: "wrap",
                height: "wrap",
                borderRadius: 1,
            }}>
                <Chip
                    label="Custom delete icon"
                    onClick={handleClick("Custom delete icon")}

                    variant="outlined"
                />
                <Chip
                    label="Custom deletse icon"
                    onClick={handleClick}

                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}

                    color="success"

                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}

                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    onDelete={handleDelete}
                    deleteIcon={<DeleteIcon/>}
                    variant="outlined"
                />
                <Chip
                    label="iconssss"
                    onClick={handleClick}
                    onDelete={handleDelete}
                    deleteIcon={<DeleteIcon/>}
                    variant="outlined"
                />
            </Stack>
        );
    }
}

export default withStreamlitConnection(ComponentSlider)
